mapper.select:
select
from stat_day_method where 1=1
and stat_date >= #{so.startDate, jdbcType=TIMESTAMP}
and stat_date <= #{so.endDate, jdbcType=TIMESTAMP}
and consumer_app_name like '%${so.consumerAppName}%'
and provider_app_name like '%${so.providerAppName}%'
and service like '%${so.service}%'
and method like '%${so.method}%'
order by
${se.orderField}
total_succ_per
${se.orderDirection}
asc
